package com.thoughtworks.data.repository;

import android.content.Context;

import com.thoughtworks.data.repository.database.CategoryInfo;
import com.thoughtworks.data.repository.database.ProductInfo;
import com.thoughtworks.domain.BaseRepository;
import com.thoughtworks.domain.product_list.usecases.GetProducts;

import java.lang.ref.WeakReference;
import java.util.Arrays;
import java.util.Collection;

import io.realm.Realm;
import io.realm.RealmConfiguration;
import io.realm.RealmQuery;
import io.realm.RealmResults;

/**
 * Created on 12-06-2018.
 */
public final class DataStoreRepository implements BaseRepository {

    public static RealmResults<ProductInfo> getProducts(final GetProducts.Category input) {
        final RealmQuery<ProductInfo> realmQuery = Realm
                .getDefaultInstance()
                .where(ProductInfo.class);
        if (input != null && input != GetProducts.Category.ALL) {
            realmQuery.equalTo(ProductInfo.ColumnNames.CATEGORY_NAME,
                    input.getName());
        }
        return realmQuery
                .findAll();
    }

    public static ProductInfo getProductForId(int prodId) {
        return Realm
                .getDefaultInstance()
                .where(ProductInfo.class)
                .equalTo(ProductInfo.ColumnNames.ID, prodId)
                .findFirst();
    }


    public static void init(final WeakReference<Context> context) {
        Realm.init(context.get());
        final RealmConfiguration config = new RealmConfiguration
                .Builder()
                .name("library.realm")
                .deleteRealmIfMigrationNeeded()
                .directory(context.get().getFilesDir())
                .modules(Realm.getDefaultModule(), new DataRealModule())
                .build();
        Realm.setDefaultConfiguration(config);
        initProductData();
    }

    private static void initProductData() {
        final Realm realm = Realm.getDefaultInstance();
        //no need to init, since already initialized
        if (realm.where(ProductInfo.class).findAll().size() > 0) {
            return;
        }
        //create category 1
        CategoryInfo electronic = new CategoryInfo(1, "Electronics");
        //create category 2
        CategoryInfo furniture = new CategoryInfo(2, "Furniture");
        realm.beginTransaction();
        Collection<ProductInfo> productInfoList =
                Arrays.asList(
                        new ProductInfo(1, "microwave oven", "Microwave",
                                10.0, electronic, "https://i5.walmartimages.com/asr/634cf459-ab85-44ec-98a3-bad23bf322d3_1.0bfec25976d5516321d7cb0039827fb7.jpeg?odnHeight=450&odnWidth=450&odnBg=FFFFFF"),
                        new ProductInfo(2, "television", "Television",
                                20.0, electronic, "data:image/jpeg;base64,"),
                        new ProductInfo(3, "vacuum cleaner", "Vacuum Cleaner",
                                30.0, electronic, "https://www.kent.co.in/images/large_image/wet-dry-vacuum-cleaner.png"),
                        new ProductInfo(4, "almirah", "Almirah",
                                40.0, furniture, "data:image/jpeg;base64,"),
                        new ProductInfo(5, "chair", "Chair",
                                50.0, furniture, "data:image/jpeg;base64,"),
                        new ProductInfo(6, "table", "Table",
                                60.0, furniture, "https://s7d4.scene7.com/is/image/roomandboard/?layer=0&size=498,300&scl=1&src=996120_wood_W&layer=comp&$prodzoom0$"));
        realm.insert(productInfoList);
        realm.commitTransaction();
        realm.close();
    }

}
